import React, { useState, useEffect } from 'react';

function UserProfile({ user }) {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchUserAds() {
      try {
        const response = await fetch(`http://localhost:8080/api/get_user_ads?user_id=${encodeURIComponent(user.id)}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads);
        } else {
          throw new Error('Failed to fetch ads');
        }
      } catch (error) {
        setError("Could not load ads' data, please try again");
        console.error('Error fetching ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchUserAds();
  }, [user.username]);

  return (
    <div>
      <h2>My Ads</h2>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <h2 style={{ marginTop: '50px' }}><strong>My ads</strong></h2>
          {ads.map((ad, index) => (
            <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>Ad id is {ad.ad_id}, {ad.pet_name} ({ad.category} - {ad.pet_gender}, age {ad.pet_age})</h3>
              <div>
                <p><strong>Ad content</strong></p>
                <p>{ad.ad_content.split('\n').map((line, i) => <span key={i}>{line}<br /></span>)}</p>
              </div>
              {ad.image_path && <img src={`http://localhost:8080${ad.image_path}`} style={{ width: '100px', height: '100px' }} />}
              <div>
                <strong>Contact:</strong> {ad.display_name} - {ad.email} - {ad.phone}
              </div>
              <div>
                <strong>Posted (US date format):</strong> {new Date(ad.created_at).toLocaleString('en-US', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UserProfile;
