import React, { useState } from 'react';

function CreateAd({ user }) {
  // State to store form data
  const [formData, setFormData] = useState({
    category: 'Cat', // Default category
    petName: '',
    petAge: '',
    petGender: 'Female', // Default gender TODO - fix since doesn't work
    adContent: '',
    image: null
  });

  // State for showing error messages
  const [error, setError] = useState('');

  const AD_CONTENT_PLACEHOLDER = "Meet Whiskers, a playful and affectionate one-year-old with a shimmering grey coat, ready for cozy cuddles and adventurous play. Fully vaccinated and neutered, this curious explorer loves chasing toys and snuggling on laps. Bring home the joyâ€”Whiskers is waiting for their forever family!"

  // Handle input changes for text inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle file change
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size > 1048576) { // Validate file size (1 MB = 1048576 bytes -> 1024 * 1024)
      setError('File size should not exceed 1MB, please select a different image');
    } else {
      setError('');
      setFormData(prevState => ({
        ...prevState,
        image: file
      }));
    }
  };

  // State for managing popup visibility and message
  const [popupSuccessfullyUploaded, setPopup] = useState({ show: false, message: '' });

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!error) {
      console.log("Submitting new ad", formData);

      const formDataObj = new FormData();
      formDataObj.append('userName', user.username);
      formDataObj.append('category', formData.category);
      formDataObj.append('petName', formData.petName);
      formDataObj.append('petAge', formData.petAge);
      formDataObj.append('petGender', formData.petGender);
      formDataObj.append('adContent', formData.adContent);
      if (formData.image) {
        formDataObj.append('image', formData.image);
      }

      try {
        const response = await fetch('http://localhost:8080/api/create_new_ad', {
          method: 'POST',
          body: formDataObj,
          // Headers are not set with 'Content-Type': 'multipart/form-data' here,
          // because the browser will automatically set it correctly based on the FormData object,
          // including the boundary string required for multipart forms.
        });
        const responseBody = await response.text();
        if (response.ok) {
          console.log('Ad was created successfully, response:', responseBody);
          setPopup({ show: true, message: 'Ad was created successfully' });
        } else {
          setPopup({ show: true, message: responseBody});
        }
      } catch (error) {
        console.error('Error during ad creation:', error);
        setPopup({ show: true, message: 'Failed to create ad ' + error});
      } finally {
        setTimeout(() => {
          setPopup({ show: false, message: '' });
        }, 3000); // 3000 ms = 3 seconds
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Ad</h2>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      <label>
        Category:
        <select name="category" value={formData.category} onChange={handleChange}>
          <option value="Dog">Dog</option>
          <option value="Cat">Cat</option>
          <option value="Reptiles">Reptiles</option>
          <option value="Fish">Fish</option>
          <option value="Rabbit">Rabbit</option>
          <option value="Guinea Pigs">Guinea Pigs</option>
          <option value="Hamster">Hamster</option>
          <option value="Gerbil">Gerbil</option>
          <option value="Ferret">Ferret</option>
          <option value="Hedgehog">Hedgehog</option>
          <option value="Pig">Pig</option>
          <option value="Horse">Horse</option>
        </select>
      </label>
      <br />
      <label>
        Pet Name:
        <input type="text" name="petName" value={formData.petName} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Pet Age:
        <input type="number" name="petAge" value={formData.petAge} onChange={handleChange} min="0" max="100" required/>
      </label>
      <br />
      <label>
        Pet Gender:
        <select name="petGender" value={formData.petGender} onChange={handleChange} required>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="other">Other</option>
        </select>
      </label>
      <br />
      <label>
        Ad Content:
        <textarea name="adContent" value={formData.adContent} onChange={handleChange} maxLength="500"  rows="10" cols="50" placeholder={AD_CONTENT_PLACEHOLDER}/>
      </label>
      <br />
      <label>
        Upload Image (jpg/jpeg/png):
        <input type="file" onChange={handleFileChange} accept="image/*" />
      </label>
      <br />
      {popupSuccessfullyUploaded.show && (
        <div style={{
          position: 'absolute',
          top: '20%', // Adjust positioning as needed
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: '#f8f9fa',
          padding: '20px',
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
        }}>
          {popupSuccessfullyUploaded.message}
        </div>
      )}
      <button
              type="submit"
              disabled={!user}
              title={!user ? "Please login to create an ad" : ""}
              style={{
                backgroundColor: !user ? '#ccc' : '#666',
                cursor: !user ? 'not-allowed' : 'pointer'
              }}
            >
              Create Ad
            </button>
    </form>
  );
}

export default CreateAd;
