import React, { useState, useEffect } from 'react';

function SeekingHome() {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchAds() {
      try {
        const response = await fetch('http://localhost:8080/api/get_all_ads', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads); // assuming the JSON response structure is { ads: [...] }
        } else {
          throw new Error('Failed to fetch ads');
        }
      } catch (error) {
        setError("Could not load ads' data, please try again");
        console.error('Error fetching ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchAds();
  }, []);

  return (
    <div>
      <h2>Seeking Home</h2>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <p><strong>All ads</strong></p>
          {ads.map((ad, index) => (
            <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>{ad.petName} ({ad.category} - {ad.petGender}, age {ad.petAge})</h3>
              <div>
                <p><strong>Ad content</strong></p>
                <p>{ad.adContent.split('\n').map((line, i) => <span key={i}>{line}<br /></span>)}</p>
              </div>
              {ad.imagePath && (
                              <div>
                                <p><strong>Image:</strong></p>
                                <img src={`http://localhost:8080${ad.imagePath}`} alt={ad.petName} style={{ width: '100px', height: '100px' }} />
                              </div>
                            )}
              <div>
                <strong>Contact:</strong> {ad.displayName} - {ad.email} - {ad.phone}
              </div>
              <div>
                <strong>Posted (US date format):</strong> {new Date(ad.createdAt).toLocaleString('en-US', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SeekingHome;
