import React, { useState, useEffect } from 'react';

function UserProfile({ user }) {

  useEffect(() => {
      async function fetchUserAds() {
        try {
          const response = await fetch('http://localhost:8080/api/get_all_ads', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json'
            },
          });
          if (response.ok) {
            const data = await response.json();
            setAds(data.ads); // assuming the JSON response structure is { ads: [...] }
          } else {
            throw new Error('Failed to fetch ads');
          }
        } catch (error) {
          setError("Could not load ads' data, please try again");
          console.error('Error fetching ads:', error);
        } finally {
          setLoading(false);
        }
      }

      fetchUserAds();
    }, []);

  return (
    <div>
      <h2>My Data</h2>
      <p>Username: {user.username}</p>
      <p>Display Name: {user.displayName}</p>
      <p>Email: {user.email}</p>
      <p>Phone: {user.phone}</p>
      {/* Add code to display user's ads here */}
    </div>
  );
}

export default UserProfile;
