import React, { useState, useEffect } from 'react';
import logo from './logo.png';
import BASE_URL from './config/config';


function SeekingHome({ user, isAuthenticated }) {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [adsPerPage, setAdsPerPage] = useState(10);
  const [category, setCategory] = useState('');
  const [totalAds, setTotalAds] = useState(0);

  useEffect(() => {
    async function fetchAds() {
      setLoading(true);
      try {
        const response = await fetch(`${BASE_URL}/get_all_ads?pageNum=${currentPage}&adsPerPage=${adsPerPage}&category=${category}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads);
          setTotalAds(data.totalAds || 0);
        } else {
          throw new Error('Failed to fetch ads');
        }
      } catch (error) {
        setError("Could not load ads' data, please try again");
        console.error('Error fetching ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchAds();
  }, [currentPage, adsPerPage, category]);

  const handleNextPage = () => {
    if (currentPage < Math.ceil(totalAds / adsPerPage)) {
      setCurrentPage(prevPage => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(prevPage => prevPage - 1);
    }
  };

  const handleAdsPerPageChange = (event) => {
    setAdsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1);
  };

  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
    setCurrentPage(1);
  };

  const handleFavorite = async (adId) => {
    if (!isAuthenticated) {
      alert('You must be logged in to favorite an ad');
      return;
    }

    console.log(`Favouring ad with ID: ${adId}`); // Add logging to debug

    try {
      const response = await fetch(`${BASE_URL}/add_ads_to_favorites?user_id=${user.id}&ad_id=${adId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      if (!response.ok) {
        const errorMsg = await response.text();
        alert(errorMsg);
        throw new Error('Failed to favorite ad');
      }

      alert('Ad favorited successfully');
    } catch (error) {
      console.error('Error favoriting ad:', error);
    }
  };

  const categories = ["Dog", "Cat", "Bird", "Duck", "Reptiles", "Fish", "Rabbit", "Guinea Pigs", "Hamster", "Gerbil", "Ferret", "Hedgehog", "Pig", "Horse"];

  return (
    <div>
      <h2>Seeking Home</h2>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <div>
            <label htmlFor="adsPerPage">Ads per page: </label>
            <select id="adsPerPage" value={adsPerPage} onChange={handleAdsPerPageChange}>
              <option value={1}>1</option>
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={20}>20</option>
              <option value={30}>30</option>
            </select>
          </div>
          <div>
            <label htmlFor="category">Category: </label>
            <select id="category" value={category} onChange={handleCategoryChange}>
              <option value="">All</option>
              {categories.map((cat) => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <p><strong>All ads</strong></p>
          {ads.map((ad) => (
            <div key={ad.ad_id} style={{ border: '1px solid #add8e6', margin: '10px', padding: '10px' }}>
              <h3>Hey! My name is {ad.pet_name} and I am a {ad.pet_gender} {ad.category} at the age of {ad.pet_age}</h3>
              <div>
                <p><strong>Ad content</strong></p>
                <p>{ad.ad_content.split('\n').map((line, i) => <span key={i}>{line}<br /></span>)}</p>
              </div>
              <img src={ad.image_path ? `http://localhost:8080${ad.image_path}` : logo} alt="Ad" style={{ width: '150px', height: 'auto', opacity: ad.image_path ? 1 : 0.5 }} />
              <div>
                <strong>Contact:</strong> {ad.display_name} - {ad.email} - {ad.phone}
              </div>
              <div>
                <strong>Posted (US date format):</strong> {new Date(ad.created_at).toLocaleString('en-US', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })}
              </div>
              <button onClick={() => handleFavorite(ad.ad_id)}>Favorite</button>
            </div>
          ))}
          <div>
            <button onClick={handlePreviousPage} disabled={currentPage === 1}>Previous</button>
            <span> Page {currentPage} of {Math.ceil(totalAds / adsPerPage) || 1} </span>
            <button onClick={handleNextPage} disabled={currentPage >= Math.ceil(totalAds / adsPerPage)}>Next</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default SeekingHome;
