import React, { useState } from 'react';

function Register() {
  // State to store input values
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    displayName: '',
    email: '',
    phone: ''
  });

  // State to toggle password visibility
    const [passwordShown, setPasswordShown] = useState(false);

  // Toggle password visibility
    const togglePasswordVisibility = () => {
      setPasswordShown(!passwordShown);
    };

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // State for managing popup visibility and message
  const [popupSuccessfullyRegistered, setPopup] = useState({ show: false, message: '' });
  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent default form submission behavior
    console.log("Submitting form", formData);

    // Here you would typically send a request to your server
    // Example POST request (you'll need to adjust the URL and config):
    /*
    try {
      const response = await fetch('http://your-java-backend/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        const data = await response.json();
        console.log('Registration successful', data);
        // Handle successful registration (e.g., redirect to login)
      } else {
        throw new Error('Failed to register');
      }
    } catch (error) {
      console.error('Error during registration:', error);
    }
    */
    
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Register</h2>
      <label>
        Username:
        <input type="text" name="username" value={formData.username} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Password:
        <input type={passwordShown ? "text" : "password"} name="password" value={formData.password} onChange={handleChange} required />
            <button type="button" onClick={togglePasswordVisibility}>
                {passwordShown ? "Hide" : "Show"}
            </button>
      </label>
      <br />
      <label>
        Display Name:
        <input type="text" name="displayName" value={formData.displayName} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Phone:
        <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
}

export default Register;
