import React, { useState } from 'react';

function Login({ onLogin }) {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // State to toggle password visibility
  const [passwordShown, setPasswordShown] = useState(false);

  // Toggle password visibility
  const togglePasswordVisibility = () => {
    setPasswordShown(!passwordShown);
  };

  const [popupSuccessfullyRegistered, setPopup] = useState({ show: false, message: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent default form submission behavior
    const formDataObj = new FormData();
    formDataObj.append('username', formData.username);
    formDataObj.append('password', formData.password);

    try {
      const response = await fetch('http://localhost:8080/api/login', {
        method: 'POST',
        body: formDataObj
      });
      if (response.ok) {
        const data = await response.json(); // Assuming server sends back JSON with user data
        console.log('login successfully');
        setPopup({ show: true, message: 'welcome ' + formData.username });

        // Call onLogin with the user data
        onLogin(data);

        // Set a timeout to hide the popup after a few seconds
        setTimeout(() => {
          setPopup({ show: false, message: '' });
        }, 3000);
      } else {
        const errorMsg = await response.text(); // Use response.text() for plain text response
        console.log('Login failed:', errorMsg);
        setPopup({ show: true, message: errorMsg });
      }
    } catch (error) {
      console.error('Network or other error', error);
      setPopup({ show: true, message: 'Failed to login due to a network or system error.' });
    } finally {
      // Automatically hide the popup after 3 seconds
      setTimeout(() => {
        setPopup({ show: false, message: '' });
      }, 3000);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Login</h2>
      <label>
        Username:
        <input type="text" name="username" value={formData.username} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Password:
        <input type={passwordShown ? "text" : "password"} name="password" value={formData.password} onChange={handleChange} required />
        <button type="button" onClick={togglePasswordVisibility}>
          {passwordShown ? "Hide" : "Show"}
        </button>
      </label>
      <br />
      {popupSuccessfullyRegistered.show && (
        <div style={{
          position: 'absolute',
          top: '20%', // Adjust positioning as needed
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: '#f8f9fa',
          padding: '20px',
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
        }}>
          {popupSuccessfullyRegistered.message}
        </div>
      )}
      <button type="submit">Login</button>
      <p style={{ marginTop: '30px', color: 'blue' }}>Don't have a user yet? You can <a href="register">register</a> here.</p>
    </form>
  );
}

export default Login;
