import React, { useState, useEffect } from 'react';

function UserProfile({ user }) {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchUserAds() {
      try {
        const response = await fetch(`http://localhost:8080/api/get_user_ads?userName=${encodeURIComponent(user.username)}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads);
        } else {
          throw new Error('Failed to fetch ads');
        }
      } catch (error) {
        setError("Could not load ads' data, please try again");
        console.error('Error fetching ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchUserAds();
  }, [user.username]);

  return (
    <div>
      <h2>My Data</h2>
      <p>
      <span style={{ textDecoration: 'underline' }} className="underline">Username:</span> {user.username}
      </p>
      <p>Display Name: {user.displayName}</p>
      <p>Email: {user.email}</p>
      <p>Phone: {user.phone}</p>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <p><strong>All ads</strong></p>
          {ads.map((ad, index) => (
            <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>{ad.petName} ({ad.category} - {ad.petGender}, age {ad.petAge})</h3>
              <div>
                <p><strong>Ad content</strong></p>
                <p>{ad.adContent.split('\n').map((line, i) => <span key={i}>{line}<br /></span>)}</p>
              </div>
              {ad.imagePath && <img src={`http://localhost:8080${ad.imagePath}`} style={{ width: '100px', height: '100px' }} />}
              <div>
                <strong>Contact:</strong> {ad.displayName} - {ad.email} - {ad.phone}
              </div>
              <div>
                <strong>Posted (US date format):</strong> {new Date(ad.createdAt).toLocaleString('en-US', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UserProfile;
