import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate
} from 'react-router-dom';
import Home from './Home';
import About from './About';
import Ads from './Ads';
import Register from './Register';
import Login from './Login';
import SearchAds from './SearchAds';
import SeekingHome from './SeekingHome';
import CreateAd from './CreateAd';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  const handleLogin = (userData) => {
    setIsAuthenticated(true);
    setUser(userData);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/ads">Ads</Link></li>
            {!isAuthenticated && <li><Link to="/register">Register</Link></li>}
            {!isAuthenticated && <li><Link to="/login">Login</Link></li>}
            {isAuthenticated && (
              <>
                <li>Welcome, {user.username}</li>
                <li><button onClick={handleLogout}>Logout</button></li>
                <li><Link to="/mydata">My Data</Link></li>
              </>
            )}
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/ads" element={<Ads />}>
            <Route path="search" element={<SearchAds />} />
            <Route path="seekingAHome" element={<SeekingHome />} />
            <Route path="newAd" element={<CreateAd />} />
          </Route>
          {!isAuthenticated && <Route path="/register" element={<Register />} />}
          {!isAuthenticated && <Route path="/login" element={<Login onLogin={handleLogin} />} />}
          {isAuthenticated && <Route path="/mydata" element={<MyData user={user} />} />}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
