import React, { useState, useEffect } from 'react';

function MyFavorites({ user }) {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchFavoritesAds() {
      try {
        const response = await fetch(`http://localhost:8080/api/get_user_favorites_ads?user_id=${encodeURIComponent(user.id)}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads);
        } else {
          throw new Error('Failed to fetch favorite ads');
        }
      } catch (error) {
        setError("Could not load favorite ads' data, please try again");
        console.error('Error fetching favorite ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchFavoritesAds();
  }, [user.id]);

  const handleDelete = async (ad_id, image_path) => {
    try {
      const response = await fetch(`http://localhost:8080/api/delete_ad?ad_id=${encodeURIComponent(ad_id)}&image_path=${encodeURIComponent(image_path)}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setAds(ads.filter(ad => ad.ad_id !== ad_id));
      } else {
        throw new Error('Failed to delete ad');
      }
    } catch (error) {
      console.error('Error deleting ad:', error);
      setError('Could not delete the ad, please try again');
    }
  };

  return (
    <div>
      <h2>My Favorites</h2>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          {ads.map((ad, index) => (
            <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>Ad id is {ad.ad_id}, {ad.pet_name} ({ad.category} - {ad.pet_gender}, age {ad.pet_age})</h3>
              <div>
                <p><strong>Ad content</strong></p>
                <p>{ad.ad_content.split('\n').map((line, i) => <span key={i}>{line}<br /></span>)}</p>
              </div>
              {ad.image_path && <img src={`http://localhost:8080${ad.image_path}`} style={{ width: '100px', height: '100px' }} />}
              <div>
                <strong>Contact:</strong> {ad.display_name} - {ad.email} - {ad.phone}
              </div>
              <div>
                <strong>Posted (US date format):</strong> {new Date(ad.created_at).toLocaleString('en-US', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })}
              </div>
              <button onClick={() => handleDelete(ad.ad_id, ad.image_path)}>Delete</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyFavorites;
