import React, { useState, useEffect } from 'react';
import { BASE_URL, ADS_PER_PAGE } from './config/config';
import Ad from './Ad';

function MyFavorites({ user }) {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [adsPerPage, setAdsPerPage] = useState(10);
  const [totalAds, setTotalAds] = useState(0);

  useEffect(() => {
    async function fetchFavoritesAds() {
      setLoading(true);
      try {
        const response = await fetch(`${BASE_URL}/get_user_favorites_ads?user_id=${encodeURIComponent(user.id)}&pageNum=${currentPage}&adsPerPage=${adsPerPage}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.ok) {
          const data = await response.json();
          setAds(data.ads);
          setTotalAds(data.totalAds || 0);
        } else {
          throw new Error('Failed to fetch favorite ads');
        }
      } catch (error) {
        setError("Could not load favorite ads' data, please try again");
        console.error('Error fetching favorite ads:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchFavoritesAds();
  }, [user.id, currentPage, adsPerPage]);

  const handleDelete = async (user_id, ad_id) => {
    try {
      const response = await fetch(`${BASE_URL}/delete_ad_from_favorites?user_id=${encodeURIComponent(user_id)}&ad_id=${encodeURIComponent(ad_id)}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        setAds(ads.filter(ad => ad.ad_id !== ad_id));
      } else {
        throw new Error('Failed to delete ad');
      }
    } catch (error) {
      console.error('Error deleting ad:', error);
      setError('Could not delete the ad, please try again');
    }
  };

  const handleNextPage = () => {
    if (currentPage < Math.ceil(totalAds / adsPerPage)) {
      setCurrentPage(prevPage => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(prevPage => prevPage - 1);
    }
  };

  const handleAdsPerPageChange = (event) => {
    setAdsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1);
  };

  return (
    <div>
      <h2>My Favorites</h2>
      {loading ? (
        <p>Loading ads...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <div>
            <label htmlFor="adsPerPage">Ads per page: </label>
            <select id="adsPerPage" value={adsPerPage} onChange={handleAdsPerPageChange}>
              {ADS_PER_PAGE.map(num => (
                <option key={num} value={num}>{num}</option>
              ))}
            </select>
          </div>
          <p><strong>My favorite ads</strong></p>
          {ads.map((ad, index) => (
            <Ad key={index} ad={ad} handleDelete={(adId) => handleDelete(user.id, adId)} />
          ))}
          <div>
            <button onClick={handlePreviousPage} disabled={currentPage === 1}>Previous</button>
            <span> Page {currentPage} of {Math.ceil(totalAds / adsPerPage) || 1} </span>
            <button onClick={handleNextPage} disabled={currentPage >= Math.ceil(totalAds / adsPerPage)}>Next</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default MyFavorites;
