import React, { useState } from 'react';

function CreateAd() {
  // State to store form data
  const [formData, setFormData] = useState({
    category: 'cat', // Default category
    petName: '',
    petAge: '',
    petGender: 'female', // Default gender
    adContent: '',
    image: null
  });

  // State for showing error messages
  const [error, setError] = useState('');

  const AD_CONTENT_PLACEHOLDER = "Meet Whiskers, a playful and affectionate one-year-old with a shimmering grey coat, ready for cozy cuddles and adventurous play. Fully vaccinated and neutered, this curious explorer loves chasing toys and snuggling on laps. Bring home the joyâ€”Whiskers is waiting for their forever family!"

  // Handle input changes for text inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle file change
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.size > 1048576) { // 1MB limit
      setError('File size should not exceed 1MB');
    } else {
      setError('');
      setFormData(prevState => ({
        ...prevState,
        image: file
      }));
    }
  };

  // State for managing popup visibility and message
    const [popupSuccessfullyUploaded, setPopup] = useState({ show: false, message: '' });

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!error) {
      console.log("Submitting form", formData);
      try {
          // Simulated successful response
          setPopup({ show: true, message: 'Ad was created successfully ' });

          // Optional: Clear form or handle server response
          setFormData({
          category: 'cat',
          petName: '',
          petAge: '',
          petGender: 'female',
          adContent: '',
          image: null
          });

          // Automatically hide the popup after 3 seconds
          setTimeout(() => {
            setPopup({ show: false, message: '' });
          }, 3000);  // 3000 ms = 3 seconds
      } catch (error) {
              // Handle errors if needed
              setPopup({ show: true, message: 'Failed to create ad' });
              setTimeout(() => {
                setPopup({ show: false, message: '' });
              }, 3000);
            }
      // Implement API call to server here
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Ad</h2>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      <label>
        Category:
        <select name="category" value={formData.category} onChange={handleChange}>
          <option value="dog">Dog</option>
          <option value="cat">Cat</option>
          <option value="bird">Bird</option>
        </select>
      </label>
      <br />
      <label>
        Pet Name:
        <input type="text" name="petName" value={formData.petName} onChange={handleChange} required />
      </label>
      <br />
      <label>
        Pet Age:
        <input type="number" name="petAge" value={formData.petAge} onChange={handleChange} min="0" max="100" />
      </label>
      <br />
      <label>
        Pet Gender:
        <select name="petGender" value={formData.petGender} onChange={handleChange}>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="other">Other</option>
        </select>
      </label>
      <br />
      <label>
        Ad Content:
        <textarea name="adContent" value={formData.adContent} onChange={handleChange} maxLength="500"  rows="10" cols="50" placeholder={AD_CONTENT_PLACEHOLDER}/>
      </label>
      <br />
      <label>
        Upload Image:
        <input type="file" onChange={handleFileChange} accept="image/*" />
      </label>
      <br />
      {popupSuccessfullyUploaded.show && (
        <div style={{
          position: 'absolute',
          top: '20%', // Adjust positioning as needed
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: '#f8f9fa',
          padding: '20px',
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
        }}>
          {popupSuccessfullyUploaded.message}
        </div>
      )}
      <button type="submit">Create Ad</button>
    </form>
  );
}

export default CreateAd;
