import React, { useState } from 'react';

function SeekingHome() {
  const [ads, setAds] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchAds = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/get_all_ads', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
      });
      if (response.ok) {
        const data = await response.json();
        setAds(data.ads); // assuming the JSON response structure is { ads: [...] }
      } else {
        throw new Error('Failed to fetch ads');
      }
    } catch (error) {
      console.error('Error fetching ads:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Seeking Home</h2>
      <p>Here you can search for ads...</p>
      <button onClick={fetchAds} disabled={loading}>
        {loading ? 'Loading...' : 'Load Ads'}
      </button>
      {ads.length > 0 && (
        ads.map((ad, index) => (
          <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
            <h3>{ad.petName}</h3>
            <p>{ad.adContent}</p>
            {ad.imagePath && <img src={ad.imagePath} alt={ad.petName} style={{ width: '100px', height: '100px' }} />}
            <div>
              <strong>Contact:</strong> {ad.displayName} - {ad.email} - {ad.phone}
            </div>
            <div><strong>Posted:</strong> {ad.createdAt}</div>
          </div>
        ))
      )}
    </div>
  );
}

export default SeekingHome;
